mapper.select:
SELECT 
	*
FROM FILE_DETAIL
WHERE FILE_DETAIL_ID = #{fileDetailId}
	AND FILE_MST_ID = #{fileMstId}

mapper.insert:
INSERT INTO FILE_DETAIL (
	FILE_MST_ID
	, FILE_DEST
	, FILE_NUMBER
	, FILE_LOCATION
	, FILE_PATH
	, FILE_NAME
	, ORG_FILE_NAME
	, FILE_EXT
	, FILE_MIME
	, FILE_SIZE
	, REG_ID
) VALUES (
	#{fileMstId}
	, #{fileDest}
	, #{fileNumber}
	, #{fileLocation}
	, #{filePath}
	, #{fileName}
	, #{orgFileName}
	, #{fileExt}
	, #{fileMiMe}
	, #{fileSize}
	, #{regID}
)
